var SharedMemory = require("./");

function str(buffer, len) {
	var a = new Uint8Array(buffer);
	var i = 0;
	// TODO (T106329635): Please fix this event handler naming convention if you see this (e.g. handleClick)
    // eslint-disable-next-line quotes
    var res = "";
	while(a[i] != 0 && i < len) {
		res += String.fromCharCode(a[i]);
		i++;
	}
	return res;
}


